<?php
 namespace App\Services; use App\Models\StoreSync; use App\Models\Table; use Illuminate\Console\Scheduling\Schedule; class StoreSyncService { static function eachSync(callable $callback) { $records = StoreSync::query()->where("\x73\x74\141\164\x75\163", 1)->get(); foreach ($records as $key => $record) { $callback($key, $record); } } static function sync(Schedule $schedule) { self::eachSync(function ($key, $item) use($schedule) { $frequency = $item->frequency_time; $schedule->call(function () use($item) { $store = StoreService::readFromStore($item->store_id); foreach ($store as $k => $record) { TableService::insert($item->table_id, $record); } })->name($item->name)->{$frequency}()->withoutOverlapping(); }); } } ?>